    function () {
        return &apos;&#xC81C; &#xC774;&#xB984;&#xC740; &quot;home&quot; &#xC785;&#xB2C8;&#xB2E4;.&apos;;
    }
]);

Route::get(&apos;/home&apos;, function () {
    return redirect(route(&apos;home&apos;));
});

