class Authenticate
{
    public function handle($request, Closure $next, $guard = null)
    {
        if (Auth::guard($guard)-&gt;guest()) {
            if ($request-&gt;ajax() || $request-&gt;wantsJson()) {
                return response(&apos;Unauthorized.&apos;, 401);
            } else {
                return redirect()-&gt;guest(&apos;login&apos;);
            }
        }

        return $next($request);
    }
}

